func tidyTx(tx *model.SignTxData) *model.SignTxData {
	tx.Data = strings.Replace(tx.Data, "0x", "", -1)
	return tx
}

// /
func (s *sRule) Exec(ctx context.Context, from string, txs []*model.SignTxData) (*v1.TxRiskRes, error) {
	for _, tx := range txs {
		rst, err := s.client.PerformRiskTx(ctx, &v1.TxRiskReq{
			Address:  from,
			Contract: tx.Target,
			TxData:   tx.Data,
		})
		if err != nil {
			return nil, err
		}
		if rst.Ok == false {
			return nil, nil
		}
	}
	return nil, nil
}

